function deleteItem(message,route,id) {

    var conf = confirm(message);

    if(conf == true){


        $.post(route,{
            id:id,
            _method: 'DELETE',
            _token: $('meta[name="csrf-token"]').attr('content')
        },function (e) {

            if(e == 1){
                location.reload();
            }else if(e == 0){
                location.reload();
            }
        },'json');

    }else{
        return;
    }
}
