@php echo Spatie\SchemaOrg\Schema::product()->name($tour->tour_header)->url(route('item.show',[app()->getLocale(),changeUrlStyle($tour->url)]))->image(asset('content/images/Tours/'. $tourId . '/'.  $tour->photo_path))->brand(\Spatie\SchemaOrg\Schema::thing()->name('My Marmaris Tours')->url(config('app.url')))->description($tour->meta_desc)->offers(array_map(function ($price) use ($tourCurrency,$tour){return \Spatie\SchemaOrg\Schema::offer()->name($tour->tour_header . ' ' . locale_words($price->age_range))->url(route('item.show',[app()->getLocale(),changeUrlStyle($tour->url)]).'#booking')->price(sprintf('%0.2f',$price->price))->priceCurrency($tourCurrency->country)->availability('http://schema.org/PreOrder');},iterator_to_array($prices)))->toScript(); @endphp