@if(isset($is_dest) && $is_dest == 1) @php $tourName='tour_name';$tourDesc = 'tour_summary';$tour_id_name = 'parent_id'; @endphp @else @php $tourName='tour_header';$tourDesc = 'tour_explain';$tour_id_name = 'tour_id'; @endphp @endif @foreach($tours as $tour) @php $currenc = get_currency_info($tour->currency_id); echo Spatie\SchemaOrg\Schema::product()->name($tour->$tourName)->url(route('item.show',[app()->getLocale(),changeUrlStyle($tour->url)]))->image(asset('content/images/Tours/'. $tour->$tour_id_name . '/'.  $tour->photo_path))->brand(\Spatie\SchemaOrg\Schema::thing()->name('My Marmaris Tours')->url(config('app.url')))->description($tour->$tourDesc)->offers(\Spatie\SchemaOrg\Schema::offer()->name($tour->$tourName)->url(route('item.show',[app()->getLocale(),changeUrlStyle($tour->url)]).'#booking')->price(sprintf('%0.2f',$tour->price))->priceCurrency($currenc->country))->toScript();  @endphp   @endforeach